package org.ict.day3;

/**
 * 
 */
public class RelationalOperators {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,c=10;
		
		System.out.println(a==c); // equality 
		System.out.println(a!=b); // not equals
		System.out.println(a<c);  // less than
		System.out.println(a<=b); // less than or equals to
		System.out.println(a>b); // greater than
		System.out.println(a>=c); // greater than or equals to

	}

}
